mapper.select:
select
  empId, empName, empSex, empPhone, empEmail, empEducation, empCardNo, empAddress, empCreateTime, empBirthday, empHobby, jobId, deptId
from pms.emp


        and empId = #{empid}


        and empName = #{empname}


        and empSex = #{empsex}


        and empPhone = #{empphone}


        and empEmail = #{empemail}


        and empEducation = #{empeducation}


        and empCardNo = #{empcardno}


        and empAddress = #{empaddress}


        and empCreateTime = #{empcreatetime}


        and empBirthday = #{empbirthday}


        and empHobby = #{emphobby}


        and jobId = #{jobid}


        and deptId = #{deptid}
